settings.custom_code:
{% comment %}
T-Shirt Size Chart with Unit and Format Toggle
Place this code in a section file or template
{% endcomment %}

<div class="size-chart-container">
 <style>
  .size-chart-container {
   max-width: 1200px;
   margin: 0 auto;
   padding: 20px;
  }

  .size-chart-image {
   width: 100%;
   height: auto;
   max-width: 400px;
   margin: 0 auto 20px;
   display: block;
   border-radius: 8px;
  }

  .size-chart-controls {
   display: flex;
   gap: 10px;
   margin-bottom: 25px;
   justify-content: center;
  }

  .toggle-group {
   display: flex;
   align-items: center;
   gap: 10px;
  }

  .size-chart-table {
   width: 100%;
   border-collapse: collapse;
   margin-top: 20px;
   overflow: hidden;
  }

  .size-chart-table th {
   padding: 12px 8px;
   text-align: center;
   font-weight: 600;
  }

  .size-chart-table td {
   padding: 10px 8px;
   text-align: center;
   border-bottom: 1px solid #dee2e6;
  }

  .size-chart-table tr:hover {
   background: #f8f9fa;
  }

  .size-chart-table .size-label {
   font-weight: 600;
  }

  .ft125 {
   font-size: 24px;
   font-weight: 700;
   color: #333;
   margin-bottom: 15px;
   text-align: center;
  }


  @media (max-width: 768px) {
   .size-chart-container {
    padding: 15px;
	margin-bottom: 80px;
   }

   .size-chart-controls {
    align-items: center;
   }

   .toggle-group {
    width: 100%;
    justify-content: center;
   }

   .size-chart-table {
    font-size: 12px;
   }

   .size-chart-table th,
   .size-chart-table td {
    padding: 8px 4px;
   }

   .ft125 {
    font-size: 20px;
   }
  }

  @media (max-width: 480px) {
   .size-chart-table {
    font-size: 11px;
   }

   .size-chart-table th,
   .size-chart-table td {
    padding: 6px 2px;
   }
  }
 </style>

 <img src="https://cdn.shopify.com/s/files/1/0903/6585/3983/files/Dimensions.webp?v=1751551347" 
    alt="T-Shirt Size Chart Dimensions" 
    class="size-chart-image"
    loading="lazy">

 <h2 class="ft125 kug">Size Chart</h2>
 <p >Choose your preferred measurement unit and sizing format below. All measurements are taken laying flat.</p>

 <div class="size-chart-controls">
  <div class="toggle-group">
   <span >Units:</span>
   <button class="toggle-btn btn-sm btn btn7 active" id="inches-btn" type="button">Inches</button>
   <button class="toggle-btn btn-sm btn btn3" id="cm-btn" type="button">CM</button>
  </div>

  <div class="toggle-group">
   <span >Format:</span>
   <button class="toggle-btn btn-sm btn btn7 active" id="us-btn" type="button">US</button>
   <button class="toggle-btn btn-sm btn btn3" id="uk-btn" type="button">UK</button>
  </div>
 </div>

 <table class="size-chart-table" id="size-chart-table">
  <thead>
   <tr>
    <th>Size</th>
    <th>Chest</th>
    <th>Length</th>
    <th>Shoulder</th>
    <th>Sleeve</th>
   </tr>
  </thead>
  <tbody id="size-chart-body"></tbody>
 </table>

 <p style="margin-top: 15px; text-align: center;">
  <strong>How to measure:</strong> Chest - across the chest under arms. Length - from shoulder to bottom hem. Shoulder - from shoulder seam to shoulder seam. Sleeve - from shoulder seam to cuff.
 </p>
</div>

<script>
document.addEventListener('DOMContentLoaded', function() {
 const sizeData = {
  us: {
   inches: [
    { size: 'XS', chest: '31-34', length: '27', shoulder: '15.5', sleeve: '8' },
    { size: 'S', chest: '34-37', length: '28', shoulder: '16', sleeve: '8.25' },
    { size: 'M', chest: '38-41', length: '29', shoulder: '17', sleeve: '8.5' },
    { size: 'L', chest: '42-45', length: '30', shoulder: '18', sleeve: '8.75' },
    { size: 'XL', chest: '46-49', length: '31', shoulder: '19', sleeve: '9' },
    { size: 'XXL', chest: '50-53', length: '32', shoulder: '20', sleeve: '9.25' },
    { size: 'XXXL', chest: '54-57', length: '33', shoulder: '21', sleeve: '9.5' }
   ],
   cm: [
    { size: 'XS', chest: '79-86', length: '68.5', shoulder: '39', sleeve: '20' },
    { size: 'S', chest: '86-94', length: '71', shoulder: '40.5', sleeve: '21' },
    { size: 'M', chest: '96-104', length: '73.5', shoulder: '43', sleeve: '21.5' },
    { size: 'L', chest: '107-114', length: '76', shoulder: '45.5', sleeve: '22' },
    { size: 'XL', chest: '117-124', length: '78.5', shoulder: '48', sleeve: '23' },
    { size: 'XXL', chest: '127-134', length: '81', shoulder: '51', sleeve: '23.5' },
    { size: 'XXXL', chest: '137-145', length: '84', shoulder: '53', sleeve: '24' }
   ]
  },
  uk: {
   inches: [
    { size: '6', chest: '31-34', length: '27', shoulder: '15.5', sleeve: '8' },
    { size: '8', chest: '34-37', length: '28', shoulder: '16', sleeve: '8.25' },
    { size: '10', chest: '38-41', length: '29', shoulder: '17', sleeve: '8.5' },
    { size: '12', chest: '42-45', length: '30', shoulder: '18', sleeve: '8.75' },
    { size: '14', chest: '46-49', length: '31', shoulder: '19', sleeve: '9' },
    { size: '16', chest: '50-53', length: '32', shoulder: '20', sleeve: '9.25' },
    { size: '18', chest: '54-57', length: '33', shoulder: '21', sleeve: '9.5' }
   ],
   cm: [
    { size: '6', chest: '79-86', length: '68.5', shoulder: '39', sleeve: '20' },
    { size: '8', chest: '86-94', length: '71', shoulder: '40.5', sleeve: '21' },
    { size: '10', chest: '96-104', length: '73.5', shoulder: '43', sleeve: '21.5' },
    { size: '12', chest: '107-114', length: '76', shoulder: '45.5', sleeve: '22' },
    { size: '14', chest: '117-124', length: '78.5', shoulder: '48', sleeve: '23' },
    { size: '16', chest: '127-134', length: '81', shoulder: '51', sleeve: '23.5' },
    { size: '18', chest: '137-145', length: '84', shoulder: '53', sleeve: '24' }
   ]
  }
 };

 let currentUnit = 'inches';
 let currentFormat = 'us';

 const inchesBtn = document.getElementById('inches-btn');
 const cmBtn = document.getElementById('cm-btn');
 const usBtn = document.getElementById('us-btn');
 const ukBtn = document.getElementById('uk-btn');

 if (inchesBtn && cmBtn && usBtn && ukBtn) {
  inchesBtn.addEventListener('click', function() { toggleUnits('inches'); });
  cmBtn.addEventListener('click', function() { toggleUnits('cm'); });
  usBtn.addEventListener('click', function() { toggleFormat('us'); });
  ukBtn.addEventListener('click', function() { toggleFormat('uk'); });

  function toggleUnits(unit) {
   currentUnit = unit;
   inchesBtn.classList.toggle('active', unit === 'inches');
   cmBtn.classList.toggle('active', unit === 'cm');
   updateTable();
  }

  function toggleFormat(format) {
   currentFormat = format;
   usBtn.classList.toggle('active', format === 'us');
   ukBtn.classList.toggle('active', format === 'uk');
   updateTable();
  }

  function updateTable() {
   const tableBody = document.getElementById('size-chart-body');
   if (!tableBody) return;

   const data = sizeData[currentFormat][currentUnit];

   tableBody.innerHTML = '';
   data.forEach(function(row) {
    const tr = document.createElement('tr');
    tr.innerHTML = `
     <td class="size-label">${row.size}</td>
     <td>${row.chest}</td>
     <td>${row.length}</td>
     <td>${row.shoulder}</td>
     <td>${row.sleeve}</td>
    `;
    tableBody.appendChild(tr);
   });
  }

  // Initialize table
  updateTable();
 }
});
</script>
